const cryptosReducer = (state = {
    crypto: [],
    loading:false
}, action) => {
    switch(action.type) {
        case 'LOADING_CRYPTOS':
            return {
                ...state,
                crypto: [...state.crypto],
                loading: true
            }
        case 'ADD_CRYPTOS':
            return {
                ...state,
                crypto: action.crypto,
                loading: false
            }
        default:
            return state
    }
}

export default cryptosReducer
