const portfoliosReducer = (state = {
    portfolios: {},
    loading:false
}, action) => {
    switch(action.type) {
        case 'LOADING_PORTFOLIO':
            return {
                ...state,
                portfolio: [...state.portfolios],
                loading: true
            }
        case 'ADD_PORTFOLIO':
            return {
                ...state,
                portfolio: action.payload,
                loading: false
            }
        default:
            return state
    }
}

export default portfoliosReducer
